targets.expr:
sum(    
    http_server_requests_seconds_count{
        application="$application",         
        instance=~"$live_instances",
        status =~ "4.."
    }    
) by (method, uri, error, status, instance) > 0

sum(    
    http_server_requests_seconds_count{
        application="$application", 
        error != "none", 
        instance=~"$live_instances"
    }    
) by (method, uri, error, status, instance) > 0

sum(
    http_server_requests_seconds_count{
        application="$application", 
        status =~ "5..", 
        instance=~"$live_instances"
    }
)by (method, uri, error, status, instance) > 0

avg(
    irate(http_server_requests_seconds_sum{
        application="$application",     
        uri !~"/actuator/prometheus|UNKNOWN", 
        instance=~"$live_instances"
    }[1m]
) /
irate(
    http_server_requests_seconds_count{
        application="$application",     
        uri !~"/actuator/prometheus|UNKNOWN", 
        instance=~"$live_instances"
    }[1m]
)) by (status, method, uri, instance, error) > 0

